import React from 'react';
import { ScrollView, Text, View,Image,StyleSheet,ImageBackground  } from 'react-native';
import Header from './src/Header';
import Login from './src/Login';
import Swipe from './src/Swipe';


const App = () => {
  return (
    <View style={styles.container}>
        <View>
      <Header/>
    </View>
    <ImageBackground source={require('./src/bg.png')} style={styles.image}>
      {/* <Text style={styles.text}>Inside</Text> */}

    <View>
    <ScrollView>



   <View>
     <Login/>
     <Text  style={{ alignSelf:'flex-end',marginRight:'6%',fontSize:'19px'}} >
  <a href="https://www.w3schools.com/"   style={{color:'#fff'}}>New to SHOPMIX+ create an account</a>
{/* <Toptab/> */}

     </Text>
   </View>
   <View style={styles.containerr}>
  <View style = {styles.backgroundContainer}>
    <Image source = {require('./src/sp.jpg')} resizeMode = 'cover' style = {styles.backdrop} />
  </View>
  <Swipe/>
  <View style = {styles.overlay}>
    {/* <Text style = {styles.headline}>It should appear in front of the Background Image</Text> */}
    <Image style = {styles.logo} source = {require('./src/splash.png')} />

  </View>

 <View style={styles.main} >
 <Image style = {styles.logo} source = {require('./src/main.jpg')} />


 </View>
 <View style={styles.main} >
 <Image style = {styles.logo} source = {require('./src/main.jpg')} />
 </View>
</View>

   </ScrollView>

    </View>

    </ImageBackground>



  </View>


  )
}
const styles = StyleSheet.create({
main:{
  flexDirection: "coloum",

},
  container: {
    flex: 1,
    flexDirection: "column"
  },
  image: {
    flex: 1,
    resizeMode: "cover",
    justifyContent: "center"
  },
  text: {
    color: "grey",
    fontSize: 30,
    fontWeight: "bold"
  },
    backgroundContainer: {

    position: 'absolute',
    top: 0,
    bottom: 0,
    left: 0,
    right: 0,
  },
  containerr: {
    flex: 1,
    alignItems: 'center',
    alignSelf:'flex-start',
    marginRight:'5%',
    // width:'50%'
  },
  overlay: {
    opacity: 0.5,
    backgroundColor: '#000000'
  },
  logo: {
    backgroundColor: 'rgba(0,0,0,0)',
    width: 160,
    height: 52
  },
  backdrop: {
    flex:1,
    flexDirection: 'column'
  },
  headline: {
    fontSize: 18,
    textAlign: 'center',
    backgroundColor: 'black',
    color: 'white'
  }

});

export default App;

