import React from 'react';
import { SafeAreaView, View, Text, StyleSheet,Button } from 'react-native';

const Header = ({ onBack, title }) => (
  <SafeAreaView style={styles.headerContainer}>
    <View style={styles.header}>
    
      <View style={styles.headerCenter}>
        <Text accessibilityRole="heading" aria-level="3" style={styles.title}>{title} SHOPMIX+</Text>
        <View  style={styles.content }>
      <Text style={{color: '#fff'}}
      onPress={() => Linking.openURL('http://google.com')}> Mission</Text>
       <Text style={{color: '#fff'}}
      onPress={() => Linking.openURL('http://google.com')}>Products/Services</Text>
        <Text style={{color: '#fff'}}
      onPress={() => Linking.openURL('http://google.com')}>Contact Us</Text>
      {/* <Button title="Mission" onPress={() => Alert.alert('Simple Button pressed')} />
      <Button title="Products/Services" onPress={() => Alert.alert('Simple Button pressed')} />
      <Button title="Contact Us" onPress={() => Alert.alert('Simple Button pressed')} /> */}
      </View>
     
      </View>
      
    </View>
  </SafeAreaView>   
);

const styles = StyleSheet.create({
  headerContainer: {
    borderBottomWidth: StyleSheet.hairlineWidth,
    // borderBottomColor: '#ff4e3f',
    backgroundColor: '#1f2d61',
  },
  header: {
    padding: 10,
    paddingVertical: 5,
    //  alignItems: 'center',
    flexDirection: 'row',
    minHeight: 50
  },
  content:{ 
    flexDirection: 'row',
    alignItems: 'center',  
    marginLeft:'90%'
    
      

  },
  headerCenter: {
    flex: 1,
    order: 2
  },
  headerLeft: {
    order: 1,
    width: 80
  },
  headerRight: {
    order: 3,
    width: 80
  },
  title: {
    fontSize: 30,
    fontWeight: '600',
    textAlign: 'left',
    color: 'white',
   
  },
});

export default Header;
