import React from 'react';
import { StyleSheet, Text, View ,Image} from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
import Swiper from 'react-native-web-swiper';
import Header from './src/Header';
import Login from './src/Login';

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  slideContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  slide1: {
    backgroundColor: 'rgb(31, 45, 97)',
  },
  slide2: {
    backgroundColor: ' rgb(43 ,64, 142)',
  },
  slide3: {
    backgroundColor: ' rgb(31, 45, 97 )',
  },
});

export default class  extends React.Component {
  render() {
    return (
      <View style={{flex:1}}>
         {/* <View style={{ flexBasis: "100%", justifyContent: "center", alignItems: "center" }}></View> */}
        <Header/>

      <Swiper
        vertical
        loop
        timeout={-2.5}
        controlsProps={{
          dotActiveStyle: { backgroundColor: 'red' },
          cellsContent: { 

            'top-right': (
            <View>
              <View >

              <Text  style={{ alignSelf:'flex-end',marginRight:'29%',fontSize:'12px'}} >
           <a href="https://www.gmail.com/"  style={{ color:'#fff'}}>New to SHOPMIX+ create an account</a>


              </Text>
              </View>
            </View>
              ),

          }
        }}
      >

          <View style={{flex:1,alignItems:"center",justifyContent:"center",backgroundColor:"rgb(31, 45, 97)"}}>
              <Text>Slide 1</Text>

          </View>
          <View style={{flex:1,alignItems:"center",justifyContent:"center",backgroundColor:" rgb(43, 64 ,142)"}}>
              <Text>Slide 2</Text>
          </View>
          <View style={{flex:1,alignItems:"center",justifyContent:"center",backgroundColor:"rgb(31, 45, 97 )"}}>
              <Text>Slide 3</Text>
          </View>
      </Swiper>

      <ScrollView>
      <Image source={require('./assets/main.png')}/>

      </ScrollView>





  </View>
    );
  }
}
