import React, { Component } from 'react';  
import { View, Text, StyleSheet, Button,Image } from 'react-native';  
import Icon from 'react-native-vector-icons/Ionicons'; 
import Swiper from 'react-native-web-swiper';
import Login from './src/Login' ;
import Header from './src/Header';
import bg from './assets/bg.png'; 
import sp from './assets/sp.jpg'; 
import main from './assets/main.png'; 
import {  
    createSwitchNavigator,  
    createAppContainer,  
} from 'react-navigation';  

import {  
   createStackNavigator  
} from 'react-navigation-stack';  

import {  createDrawerNavigator} from 'react-navigation-drawer';

// const styles = StyleSheet.create({
//   container: {
//     flex: 1,
//   },
//   slideContainer: {
//     flex: 1,
//     alignItems: 'center',
//     justifyContent: 'center',
//   },

// });
export default class Toptab extends Component {  
    render() {  
        return <AppContainer />;  
    }  
}  

class WelcomeScreen extends Component {  
    static navigationOptions = {  
         title: 'Welcome',  
    };  
    render() {  
        return (  
            <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>  

            <Swiper
     horizontal   
     loop
        timeout={-2.5}
        controlsProps={{
          dotActiveStyle: { backgroundColor: 'red' },
          cellsContent: { 

            'top-right': (
            <View>
              <View >
              {/* <Login/> */}
              <Text  style={{ alignSelf:'flex-end',marginRight:'29%',fontSize:'12px'}} >
           <a href="https://www.gmail.com/"  style={{ color:'#fff'}}>New to SHOPMIX+ create an account</a>


              </Text>
              </View>
            </View>
            ),

          }
        }}
      >

          <View>
          <Image source={bg} style={{ width: 305, height: 159 }} /> 
              <Text>Slide 1</Text>

          </View>
          <View>
          <Image source={sp} style={{ width: 305, height: 159 }} /> 
              <Text>Slide 1</Text>

          </View>
          <View>
          <Image source={main} style={{ width: 305, height: 159 }} /> 
              <Text>Slide 1</Text>

          </View>
      </Swiper>
                <Button  
                    title="Go to DashboardScreen"  
                    onPress={() => this.props.navigation.navigate('Dashboard')}  
                />  
            </View>  
        );  
    }  
}  

class DashboardScreen extends Component {  
    static navigationOptions = {  
         title: 'Dashboard',  
    };  

    render() {  
        return (  
            <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>  
                        <View >
            <Login/>
             <Text  style={{ alignSelf:'flex-end',marginRight:'29%',fontSize:'12px'}} >
            <a href="https://www.gmail.com/"  style={{ color:'#fff'}}>New to SHOPMIX+ create an account</a>


             </Text>
               </View>

            </View>  
        );  
    }  
}  
const DashboardStackNavigator = createStackNavigator(  
    {  
        DashboardNavigator: DashboardScreen  
    },  
    {  
        defaultNavigationOptions: ({ navigation }) => {  
        return {  
            headerLeft: (  
                <Icon  
                    style={{ paddingLeft: 10 }}  
                    onPress={() => navigation.openDrawer()}  
                    name="md-menu"  
                    size={30}  
                />  
            )  
        };  
        }  
    }  
);  

const WelcomeStackNavigator = createStackNavigator(  
    {  
        WelcomeNavigator: WelcomeScreen  
    },  
    {  
        defaultNavigationOptions: ({ navigation }) => {  
            return {  
                headerLeft: (  
                    <Icon  
                        style={{ paddingLeft: 10 }}  
                        onPress={() => navigation.openDrawer()}  
                        name="md-menu"  
                        size={30}  
                    />  
                )  
            };  
        }  
    }  
);  
const AppDrawerNavigator = createDrawerNavigator({  
    Dashboard: {  
        screen: DashboardStackNavigator  
    },  
    Welcome: {  
        screen: WelcomeStackNavigator  
    },  
});  

const AppSwitchNavigator = createSwitchNavigator({  
    Dashboard: { screen: AppDrawerNavigator },  
    Welcome: { screen: WelcomeScreen },  

});  

const AppContainer = createAppContainer(AppSwitchNavigator);  

const styles = StyleSheet.create({  
    container: {  
        flex: 1,  
        alignItems: 'center',  
        justifyContent: 'center'  
    }  
});
