import React from 'react';
import { StyleSheet, Text, View ,Image} from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
import Swiper from 'react-native-web-swiper';
import Header from './src/Header';
//  import Toptab from './src/Toptab';
 import bg from '../assets/bg.png'; 
 import sp from './assets/sp.jpg'; 
 import main from './assets/main.png'; 
const style = StyleSheet.create({
  container: {
    flex: 1,
  },
  slideContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
 
});
 
export default class Mission  extends React.Component {
  render() {
    return (
      
      <View style={{flex:1}}>
         {/* <View style={{ flexBasis: "100%", justifyContent: "center", alignItems: "center" }}></View> */}
         <Header/>
      
      <Swiper
     horizontal   
     loop
        timeout={-2.5}
        controlsProps={{
          dotActiveStyle: { backgroundColor: 'red' },
          cellsContent: { 
           
            'top-right': (
            <View>
              <View >
              {/* <Login/> */}
              <Text  style={{ alignSelf:'flex-end',marginRight:'29%',fontSize:'12px'}} >
           <a href="https://www.gmail.com/"  style={{ color:'#fff'}}>New to SHOPMIX+ create an account</a>
     
         
              </Text>
              </View>
            </View>
            ),
            
          }
        }}
      >
       
          <View>
          <Image source={bg} style={{ width: 305, height: 159 }} /> 
              <Text>Slide 1</Text>
              
          </View>
          <View>
          <Image source={sp} style={{ width: 305, height: 159 }} /> 
              <Text>Slide 1</Text>
              
          </View>
          <View>
          <Image source={main} style={{ width: 305, height: 159 }} /> 
              <Text>Slide 1</Text>
              
          </View>
      </Swiper>
     
      <ScrollView>
    

      </ScrollView>
      
              
   
    

  </View>
    );
  }
}
