import React from 'react';
import { StyleSheet, Text, View ,Image} from 'react-native';
import { ScrollView, } from 'react-native-gesture-handler';
import Swiper from 'react-native-web-swiper';
import Header from './src/Header';
import Login from './src/Login';
import Cardv from './src/Cardv';
const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  slideContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  slide1: {
    backgroundColor: 'rgba(20,20,200,0.3)',
  },
  slide2: {
    backgroundColor: 'rgba(20,200,20,0.3)',
  },
  slide3: {
    backgroundColor: 'rgba(200,20,20,0.3)',
  },
});

export default class App extends React.Component {
  render() {
    return (
      <View style={{flex:1}}>
      <Swiper
        from={1}
        minDistanceForAction={0.1}
        controlsProps={{
          dotsTouchable: true,
          prevPos: 'left',
          nextPos: 'right',
          nextTitle: '>',
          nextTitleStyle: { color: 'red', fontSize: 24, fontWeight: '500' },
          PrevComponent: ({ onPress }) => (
            <TouchableOpacity onPress={onPress}>
              <Text style={{ color: 'white', fontSize: 24, fontWeight: '500' }}>
                {'<'}
              </Text>
            </TouchableOpacity>
          ),
        }}
      >
          <View style={{flex:1,alignItems:"center",justifyContent:"center",backgroundColor:"rgba(20,20,200,0.3)"}}>
              <Text>Slide 1</Text>
          </View>
          <View style={{flex:1,alignItems:"center",justifyContent:"center",backgroundColor:"rgba(20,200,20,0.3)"}}>
              <Text>Slide 2</Text>
          </View>
          <View style={{flex:1,alignItems:"center",justifyContent:"center",backgroundColor:"rgba(200,20,20,0.3)"}}>
              <Text>Slide 3</Text>
          </View>
      </Swiper>
  </View>
    );
  }
}
