import React from "react";
import { Text, TouchableOpacity, View,StyleSheet,Image } from "react-native";
import Swiper from "react-native-web-swiper";
 import bg from '../assets/bg.png'; 
 import sp from '../assets/sp.jpg'; 
 import main from '../assets/main.png'; 
const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  slideContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
 
});
class Swipe extends React.Component {
    render() {
        return (
          <View style={styles.container} >
          <View style={{flex:1}}>
              <View style={{flex:1}}>
              <Swiper
     horizontal   
     loop
        timeout={-2.5}
        controlsProps={{
          dotActiveStyle: { backgroundColor: 'red' },
          cellsContent: { 
           
            'top-right': (
            <View>
              {/* <View >
              <Login/>
              <Text  style={{ alignSelf:'flex-end',marginRight:'29%',fontSize:'12px'}} >
           <a href="https://www.gmail.com/"  style={{ color:'#fff'}}>New to SHOPMIX+ create an account</a>
     
         
              </Text>
              </View> */}
            </View>
            ),
            
          }
        }}
      >
       
          <View>
          <Image source={bg} style={{ width: 305, height: 159 }} /> 
              <Text>Slide 1</Text>
              
          </View>
          <View>
          <Image source={sp} style={{ width: 305, height: 159 }} /> 
              <Text>Slide 1</Text>
              
          </View>
          <View>
          <Image source={main} style={{ width: 305, height: 159 }} /> 
              <Text>Slide 1</Text>
              
          </View>
      </Swiper> 
      
              </View>
          </View>
          </View>
        )
    }
}

export default  Swipe;
