import * as React from 'react';
import { View, StyleSheet, Dimensions } from 'react-native';
import { TabView, SceneMap } from 'react-native-tab-view';
 import Flat from './.expo/src/Flat';
// const FirstRoute = () => (

//   <View style={[styles.scene,  <View> <Car/> </View>  
// ]} 


//   />

// );
const FirstRoute  = () => (
  <View style={[styles.scene, { backgroundColor: '#ff4085' },

    //  <Flat> </Flat>

 ]} />
);

const SecondRoute = () => (
  <View style={[styles.scene, { backgroundColor: '#ff4085' }]} />
);
const ThirdRoute = () => (
  <View style={[styles.scene, { backgroundColor: '#ff4090' }]} />
);
const initialLayout = { width: Dimensions.get('window').width };

export default function Toptab() {
  const [index, setIndex] = React.useState(0);
  const [routes] = React.useState([
    { key: 'first', title: ' Mission' },
    { key: 'second', title: 'Services/Product' },
    { key: 'third', title: 'contactor' },
  ]);

  const renderScene = SceneMap({
    first: FirstRoute,
    second: SecondRoute,
    third:ThirdRoute
  });

  return (
    <TabView
      navigationState={{ index, routes }}
      renderScene={renderScene}
      onIndexChange={setIndex}
      initialLayout={initialLayout}
    />

  );
}

const styles = StyleSheet.create({
  scene: {
    flex: 1,
  },
});
