import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import Swiper from 'react-native-web-swiper';
import Header from './src/Header';
import Login from './src/Login';
const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  slideContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  slide1: {
    backgroundColor: 'rgba(20,20,200,0.3)',
  },
  slide2: {
    backgroundColor: 'rgba(20,200,20,0.3)',
  },
  slide3: {
    backgroundColor: 'rgba(200,20,20,0.3)',
  },
});

export default class  extends React.Component {
  render() {
    return (
      <View style={{flex:1}}>
        <Header/>

      <Swiper
        vertical
        loop
        timeout={-2.5}
        controlsProps={{
          dotActiveStyle: { backgroundColor: 'red' },
          cellsContent: { 

            'top-right': (<View>


              <View >
              <Login/>
              <Text  style={{ alignSelf:'flex-end',marginRight:'4%',fontSize:'19px'}} >
           <a href="https://www.w3schools.com/"  >New to SHOPMIX+ create an account</a>
         {/* <Toptab/> */}

              </Text>
              </View>
            </View>
              ),

          }
        }}
      >

          <View style={{flex:1,alignItems:"center",justifyContent:"center",backgroundColor:"rgba(20,20,200,0.3)"}}>
              <Text>Slide 1</Text>
          </View>
          <View style={{flex:1,alignItems:"center",justifyContent:"center",backgroundColor:"rgba(20,200,20,0.3)"}}>
              <Text>Slide 2</Text>
          </View>
          <View style={{flex:1,alignItems:"center",justifyContent:"center",backgroundColor:"rgba(200,20,20,0.3)"}}>
              <Text>Slide 3</Text>
          </View>
      </Swiper>



  </View>
    );
  }
}
