import React from 'react';
import { StyleSheet, Text, View, Button, Image ,ImageBackground } from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';
import Logo from './assets/splash.png';

export default class App extends React.Component {
  render() {
    return (

     <View style={styles.container}>
         <ScrollView>
         <ImageBackground source={require('./.expo/imgs/bg.jpg')} style={styles.image}>
        <Text style={styles.h1}>SHOPMIX+</Text>

         <Text style={styles.h3}>Mission          Services/products         Contractors </Text>
         <Text
            style={styles.h3}
            onPress={() => {
              Linking.openURL('https://aboutreact.com');
            }}/>

        {/* <Text style={styles.h2}></Text> */}
        <Image
          source={Logo}
          style={styles.image}
        />
        </ImageBackground>
          </ScrollView>
        <Button
          title="LET'S START"
          style={styles.button}
          onPress={() => this.onPress()}
          color="#fff"
        />
      </View>

    );
  }
}
const styles = StyleSheet.create({

  h1: {
    color: '#fff',
    fontSize: 40,
    flex:8,
    justifyContent:'center'
  },
  h3:{
    color:'#fff',
    fontSize: 18,
    marginRight:900,
    flex:26
  },

  // container: {

  //   justifyContent: 'space-between',
  //   backgroundColor: '#000',
  //    alignItems: 'center',
  //   width: '100%',
  // },
  buttonContainer: {
    backgroundColor: '#008F68',
    borderRadius: 5,
    padding: 8,
    margin: 8,
  },
  image: {
    width: 350,
    height: 300,
    // justifyContent: 'center',
  },

  h2: {
    color: '#FAE042',
    fontSize: 18,
    marginTop: 8,

  },

  topContainer: {
    flex: 2,
    justifyContent: 'center',
    alignItems: 'center',
  },
  middleContainer: {
    flex: 3,
    justifyContent: 'flex-start',
    alignItems: 'center',
  },
  bottomContainer: {
    justifyContent: 'flex-end',
    width: '90%',
    margin: 20,
    padding: 10,
  },
});
